import React from 'react'
import './App.css'
import SpotifyWebApi from 'spotify-web-api-js'
import styled from 'styled-components'
import Header from './Components/Header'
import Sidebar from './Components/Sidebar'
import Content from './Components/Content'

const spotifyWeb = new SpotifyWebApi()

function App () {
  let hashParams = {}

  const getHashParams = () => {
    var e,
      r = /([^&;=]+)=?([^&;]*)/g,
      q = window.location.hash.substring(1)
    e = r.exec(q)
    while (e) {
      hashParams[e[1]] = decodeURIComponent(e[2])
      e = r.exec(q)
    }
    console.log('hashparams', hashParams)
    return hashParams.access_token
  }

  getHashParams()

  const spotifyLogin2 = async () => {
    const access_t = getHashParams()
    const result = await fetch(
      `https://api.spotify.com/v1/me/top/artists?time_range=medium_term&limit=10&offset=5`,
      {
        method: 'GET',
        headers: {
          Authorization: 'Bearer ' + access_t
        }
      }
    )

    if (access_t) {
      spotifyWeb.setAccessToken(access_t)
      spotifyWeb
        .getUserPlaylists() // note that we don't pass a user id
        .then(
          function (data) {
            console.log('User playlists', data)
          },
          function (err) {
            console.error(err)
          }
        )
    }
    let data = await result.json()
    return data.items
  }

  return (
    <Container>
      <Header />
      <Container>
        <Sidebar />
        <Content />
      </Container>
      <Container>
        <body>
          <script src='https://sdk.scdn.co/spotify-player.js' />
        </body>
        <div>
          <a href='http://localhost:8888'>
            <button>Login with Spotify</button>
          </a>
          <button onClick={() => spotifyLogin2()}>get info</button>
        </div>
      </Container>
    </Container>
  )
}

export default App

export const Container = styled.div`
  display: flex;
  flex-direction: column;
  background-color: red;
`

export const Wrapper = styled.div`
  display: flex;
  flex-direction: row;
  background-color: green;
`
