import React from 'react'
import styled from 'styled-components'

export const Content = ({ musicData, topArtistData }) => {
  console.log('musicdata', musicData)
  console.log('topaeasodkwejfpewf', topArtistData)
  return (
    <Container>
      <Box>
        <h1>DISCOVER</h1>
        <h2>My top artists</h2>
        {/* {topArtistData.items &&
          topArtistData.items.map(artist => <p>{artist.name}</p>)} */}
        <Wrapper>
          {topArtistData.items &&
            topArtistData.items.map(
              (artist, index) =>
                index < 5 && (
                  <ArtistBox>
                    <img
                      style={{
                        width: '60px',
                        height: '60px',
                        borderRadius: '10px'
                      }}
                      src={artist.images[0].url}
                    ></img>
                    <ArtistTitle>{artist.name}</ArtistTitle>
                  </ArtistBox>
                )
            )}
        </Wrapper>
        <h2>Based on your music-taste</h2>
      </Box>
    </Container>
  )
}

export default Content

export const Container = styled.div`
  display: flex;
  padding: 2%;
`

export const ArtistTitle = styled.p`
  color: white;
`

export const Box = styled.div`
  display: flex;
  flex-direction: column;
`

export const Wrapper = styled.div`
  display: flex;
  flex-direction: row;
  width: 50%;
`

export const ArtistBox = styled.div`
  display: flex;
  flex-direction: row;
  width: 400px;
  margin-right: 20px;
  border-radius: 10px;
  background-color: #333333;
  box-shadow: 5px 5px 15px #888888;
`
