import React, { useEffect, useState } from 'react'
import styled from 'styled-components'
import axios from 'axios'

export const FindFriends = ({
  friends,
  setAddFriend,
  me,
  addFriend,
  personalData,
  allUsers
}) => {
  //hente ut folk som er lagret i databasen og legge til
  const [users, setUsers] = useState([])
  const [added, setAdded] = useState()
  const [myFriends, setMyFriends] = useState([])
  const getUsers = () => {
    axios.get('http://localhost:8000/api/user').then(res => {
      setUsers(res.data.data)
    })
  }
  useEffect(() => {
    getUsers()
  }, [])

  const m = new Map()
  useEffect(() => {
    users.forEach(user => {
      me &&
        me.friends &&
        me.friends.forEach(friend => {
          if (user.userName === friend.userName) {
            myFriends.push(user.userName)
            //setMyFriends([...myFriends, user.userName])
          }
        })
    })
  }, [me, users, me.friends])

  let f = false
  const addFriendToUser = user => {
    /*     user.userName
     */ friends.forEach(friend =>
      friend.userName === user.userName
        ? false
        : axios
            .put(
              'http://localhost:8000/api/user/' + personalData.display_name,
              {
                friends: user
              }
            )
            .then(setAddFriend(!addFriend))
    )
  }

  const compareUsers = () => {
    const myArtistsLists = new Map()
    var scoreMap = new Map()
    var leng

    for (var i = 0; i < allUsers.length; i++) {
      var user = allUsers[i]
      leng = user.artists.length
      myArtistsLists[user.userName] = user.artists
    }

    for (var f = 0; f < allUsers.length; f++) {
      var userscore = 0
      for (var i = 0; i < leng; i++) {
        for (var j = 0; j < leng; j++) {
          //console.log(myArtistsLists[me.userName].artists[i])
          if (
            myArtistsLists[me.userName][i].name ===
            myArtistsLists[allUsers[f].userName][j].name
          ) {
            userscore++
          }
        }
      }

      scoreMap[allUsers[f].userName] = (userscore / leng) * 100
    }
    //console.log('score', scoreMap)

    return scoreMap
  }

  const scoreMap = compareUsers()
  const arrayOfFriendscores = []
  //let sortedArrayOfFriends = []
  //const artistMap = new Map()

  const sortFriends = () => {
    for (var f = 0; f < allUsers.length; f++) {
      let userName = allUsers[i].userName
      scoreMap[userName]
      const friendscore = {
        name: userName,
        score: scoreMap[userName]
      }
    }

    console.log('kalles denne???', scoreMap)
    for (var i = 0; i < leng; i++) {
      scoreMap
    }
    scoreMap.forEach((object, index) => {
      console.log('compareUsers', object, index)
      const artistObject = {
        name: index,
        listeners: object,
        length: object.length
      }
      arrayOfArtists.push(artistObject)
    })
    let sorted = arrayOfArtists.sort((a, b) => {
      return b.listeners.length - a.listeners.length
    })
    //sortedArrayOfFriends = sorted
    return sorted
  }
  useEffect(() => {
    sortFriends()
  }, [me, users])
  return (
    <div>
      <h1>Add Users</h1>
      {/*       <h2>USERS: </h2>
       */}{' '}
      {users &&
        users.map(
          (user, index) =>
            user.userName !== me.userName && (
              <div key={index}>
                <Text>{user.userName}</Text>
                <p>your matching score is {scoreMap[user.userName]}%</p>

                {myFriends && !myFriends.includes(user.userName) && (
                  <Button onClick={() => addFriendToUser(user)}>
                    Add user
                  </Button>
                )}
              </div>
            )
        )}
    </div>
  )
}

export default FindFriends

export const Button = styled.button`
  width: 140px;
  height: 30px;
  border-radius: 10px;
  background-color: #ff5fa2;
  box-shadow: 5px 5px 15px #888888;
  border-color: #ff5fa2;
  text-transform: uppercase;
  font-size: 12px;
  letter-spacing: 3px;
  color: white;
  transition: 0.5s ease;
  outline: none;
  &: hover {
    letter-spacing: 5px;
  }
`
export const Text = styled.p`
  font-size: 18px;
  text-transform: uppercase;
  letter-spacing: 3px;
`
