import React, { useEffect, useState } from 'react'
import styled from 'styled-components'
import axios from 'axios'

export const FindFriends = ({ me, personalData }) => {
  //hente ut folk som er lagret i databasen og legge til
  const [users, setUsers] = useState([])
  const [added, setAdded] = useState()

  const getUsers = () => {
    axios.get('http://localhost:8000/api/user').then(res => {
      res.data.data.forEach(user => {})
      setUsers(res.data.data)
    })
    console.log('user', users)
  }
  useEffect(() => {
    getUsers()
  }, [])

  let f = false
  const addFriend = user => {
    axios.put('http://localhost:8000/api/user/' + personalData.display_name, {
      friends: user
    })
  }
  return (
    <div>
      <h1>Add Users</h1>
      <h2>USERS: </h2>
      {users &&
        users.map(
          (user, index) =>
            user.userName !== me.userName && (
              <div key={index}>
                <p>{user.userName}</p>
                {me.friends.forEach(friend => console.log('friend: ', friend))}

                {me.friends.map(friend =>
                  user.userName == friend.userName ? (f = false) : (f = true)
                )}
                <button onClick={() => addFriend(user)}>{f}</button>
              </div>
            )
        )}
    </div>
  )
}

export default FindFriends
