  }

  compareUsers()
  return (
    <div>
      <h1>Add Users</h1>
      <h2>USERS: </h2>
      {users &&
        users.map((user, index) => (
          <div key={index}>
            <p>{user.userName}</p>
            <button onClick={() => addFriend(user)}>add</button>
          </div>
        ))}
    </div>
  )
}

