import React, { useEffect, useState } from 'react'
import styled from 'styled-components'
import axios from 'axios'

export const Sidebar = ({
  loggedIn,
  checkedOption,
  setCheckedOption,
  personalData,
  setAddFriendPage,
  me,
  setMe,
  users,
  setUsers,
  friends,
  setFriends
}) => {
  const handleOptionChange = e => {
    setCheckedOption(e.target.value)
  }

  const getUsers = async () => {
    await axios.get('http://localhost:8000/api/user').then(res => {
      setUsers(res.data.data)
    })
  }

  const addFriends = () => {
    setAddFriendPage(true)
  }

  useEffect(() => {
    getUsers()
  }, [])

  useEffect(() => {
    if (users) {
      users.forEach(
        user => user.userName === personalData.display_name && setMe(user)
      )
    }
  }, [users, personalData])

  const deleteUsers = () => {
    axios
      .delete('http://localhost:8000/api/users')
      .then(res => console.log('res', res))
  }
  return (
    <Container width='20%' height='100vh'>
      {loggedIn && (
        <div>
          <Container>
            <H3>PROFILE</H3>
            <img
              style={{ width: '70px', height: '70px', borderRadius: '50%' }}
              src={
                personalData.images &&
                personalData.images[0] &&
                personalData.images[0].url
              }
            ></img>
          </Container>
          <Container>
            {/*             <button onClick={deleteUsers}>delete users</button>
             */}
            <H3>FILTER</H3>
            <form>
              <Wrapper>
                <label style={{ color: 'white', width: '200px' }}>
                  <input
                    type='radio'
                    value='myfavorites'
                    checked={checkedOption === 'myfavorites'}
                    onChange={handleOptionChange}
                  />
                  My favorites
                </label>
              </Wrapper>
              <div className='radio'>
                <label style={{ color: 'white', width: '200px' }}>
                  <input
                    type='radio'
                    value='globalfavorites'
                    checked={checkedOption === 'globalfavorites'}
                    onChange={handleOptionChange}
                  />
                  Global favorites
                </label>
              </div>
              <div className='radio'>
                <label style={{ color: 'white', width: '200px' }}>
                  <input
                    type='radio'
                    value='friendsfavorites'
                    checked={checkedOption === 'friendsfavorites'}
                    onChange={handleOptionChange}
                  />
                  Friends favorites
                </label>
              </div>
            </form>
            <Container>
              <H3>Network</H3>

              {me &&
                me.friends &&
                me.friends.map((friend, index) => (
                  <FriendBox color={friend} key={index}>
                    <p>{friend}</p>
                    {console.log('freind', friend)}
                  </FriendBox>
                ))}
              <button
                style={{
                  backgroundColor: '#333333',
                  border: 'none',
                  outline: 'none'
                }}
                onClick={addFriends}
              >
                <img
                  style={{
                    width: '70px',
                    height: '70px',
                    borderRadius: '5px'
                  }}
                  src='addProfile.svg'
                />
              </button>
            </Container>
          </Container>
        </div>
      )}
    </Container>
  )
}

export default Sidebar

export const Container = styled.div`
  display: flex;
  flex-direction: column;
  width: ${props => props.width};
  height: ${props => props.height};
  margin-left: 3%;
  margin-right: 3%;
`

export const FriendBox = styled.div`
  width: 100px;
  height: 70px;
  margin-bottom: 10px;
  border-radius: 5px;
  background-color: ${props =>
    props.color === 'sofie123' ? '#16775E' : 'blue'};
`

export const H3 = styled.h3`
  color: lightgrey;
  font-size: 14px;
  font-style: bold;
  letter-spacing: 1px;
  text-transform: uppercase;
`

export const Category = styled.p`
  color: lightgrey;
  font-size: 16px;
  margin: 3px;
`

export const Wrapper = styled.div`
  display: flex;
  flex-direction: row;
`
